const contactUsBtn=document.getElementById("contact-us"),routeToUsBtn=document.querySelector("a.map"),contactFormModal=document.querySelector(".modal.contact-form"),routeToUsMapModal=document.querySelector(".modal.map"),routeToUsMapModalCloseBtn=document.querySelector(".modal.map button.modal-close"),contactFormModalCloseBtn=document.querySelector(".modal.contact-form button.modal-close"),form=document.querySelector(".contact-us"),name=document.querySelector(".input-name"),email=document.querySelector(".input-email"),text=document.querySelector(".input-text"),popup=document.querySelector(".contact-form .modal-content");contactUsBtn.addEventListener("click",event=>{event.preventDefault(),contactFormModal.classList.toggle("hidden")}),routeToUsBtn.addEventListener("click",()=>{event.preventDefault(),routeToUsMapModal.classList.toggle("hidden")}),routeToUsMapModalCloseBtn.addEventListener("click",()=>routeToUsMapModal.classList.toggle("hidden")),contactFormModalCloseBtn.addEventListener("click",()=>{contactFormModal.classList.toggle("hidden"),popup.classList.remove("modal-error")});const updateClasses=(list,slideIndexToDisplay)=>{list.forEach((slide,slideIndex)=>{slideIndex===slideIndexToDisplay?slide.classList.add("current"):slide.classList.remove("current")})},addToggleLogic=(controllers,slides)=>{controllers.forEach((button,index)=>{button.addEventListener("click",()=>{updateClasses(controllers,index),updateClasses(slides,index)})})},sliderControllers=document.querySelectorAll(".slider-controllers button"),slides=document.querySelectorAll(".featured-products .product");addToggleLogic(sliderControllers,slides);const servicesControllers=document.querySelectorAll(".services button"),servicesSlides=document.querySelectorAll(".services .service");addToggleLogic(servicesControllers,servicesSlides),form.addEventListener("submit",function(evt){name.value&&email.value&&text.value||(evt.preventDefault(),popup.classList.add("modal-error"))});